@tailwind base;
@tailwind components;
@tailwind utilities;

/* KIT-Canteen Design System - Zomato Inspired */

@layer base {
  :root {
    /* Core Brand Colors */
    --background: 0 0% 100%;
    --foreground: 0 0% 13%;

    /* Zomato Red Theme */
    --primary: 349 84% 57%; /* #E23744 */
    --primary-light: 349 84% 67%;
    --primary-dark: 349 84% 47%;
    --primary-foreground: 0 0% 98%;


    /* UI Components */
