        {/* Trending Now */}
        {trendingItems.length > 0 && (
          <div className="animate-fade-in">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold flex items-center">
                <Flame className="w-5 h-5 mr-2 text-orange-500" />
