import { useLocation, useParams } from "wouter";
import { useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  ArrowLeft, 
  MapPin, 
  Clock, 
  Receipt, 
  Star,
  User,
  Phone,
  CreditCard,
  Package
} from "lucide-react";
import JsBarcode from 'jsbarcode';

// Real Barcode Generator Component using JsBarcode library
const BarcodeGenerator = ({ orderId }: { orderId: string }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    if (canvasRef.current) {
      try {
        // Generate a proper Code 128 barcode
        JsBarcode(canvasRef.current, orderId, {
          format: "CODE128",
          width: 2,
          height: 60,
          displayValue: true,
          background: "#ffffff",
          lineColor: "#000000",
          margin: 10,
          fontSize: 14,
          textAlign: "center",
          textPosition: "bottom"
        });
      } catch (error) {
        // Barcode generation failed - using fallback display
        // Fallback: display order ID as text
        const canvas = canvasRef.current;
        const ctx = canvas?.getContext('2d');
        if (ctx && canvas) {
          canvas.width = 250;
          canvas.height = 80;
          ctx.fillStyle = '#ffffff';
          ctx.fillRect(0, 0, canvas.width, canvas.height);
          ctx.fillStyle = '#000000';
          ctx.font = '16px monospace';
          ctx.textAlign = 'center';
          ctx.fillText(`Order: ${orderId}`, canvas.width/2, canvas.height/2);
        }
      }
    }
  }, [orderId]);

  return (
    <canvas 
      ref={canvasRef}
      className="mx-auto"
      style={{ maxWidth: '100%', height: 'auto' }}
    />
  );
};

export default function OrderDetailPage() {
  const [, setLocation] = useLocation();
  const { orderId } = useParams();

  // Mock order data - in real app, fetch based on orderId
  const orderDetails = {
    id: orderId || "A1B2C3D4E5F6", // Use 12-digit alphanumeric format
    status: "completed",
    placedAt: "Yesterday, 1:15 PM",
    deliveredAt: "Yesterday, 1:35 PM",
    total: 60,
    subtotal: 55,
    tax: 3,
    deliveryFee: 2,
    items: [
      {
        id: 1,
        name: "Samosa",
        quantity: 2,
        price: 20,
        total: 40,
        image: "ü•ü",
        isVeg: true
      },
      {
        id: 2,
        name: "Filter Coffee",
        quantity: 1,
        price: 20,
        total: 20,
        image: "‚òï",
        isVeg: true
      }
    ],
    restaurant: {
      name: "Main Canteen",
      address: "Ground Floor, Academic Block"
    },
    customer: {
      name: "Rahul Kumar",
      phone: "+91 98765 43210"
    },
    payment: {
      method: "UPI",
      transactionId: "TXN123456789"
    },
    rating: 4.5,
    feedback: "Great food and quick service!"
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "preparing": return "bg-warning text-warning-foreground";
      case "ready": return "bg-success text-success-foreground";
      case "completed": return "bg-muted text-muted-foreground";
      case "delivered": return "bg-success text-success-foreground";
      default: return "bg-muted text-muted-foreground";
    }
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-white border-b px-4 py-4 sticky top-0 z-10">
        <div className="flex items-center space-x-4">
          <Button variant="ghost" size="icon" onClick={() => {
            // Use browser's back functionality, but with fallback
