client/src/components/RateReviewPage.tsx.content:
import React, { useState } from 'react';
import { useLocation } from 'wouter';
import { ArrowLeft, Star, Camera, Send } from 'lucide-react';
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

const RateReviewPage = () => {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  // Get orderId from URL search params
  const urlParams = new URLSearchParams(window.location.search);
  const orderId = urlParams.get('orderId');

  // Mock order data
  const orderData = {
    id: orderId || 'ORD001',
    items: [
      {
        id: 1,
        name: 'Grilled Chicken Burger',
        image: '/placeholder.svg'
      },
      {
        id: 2,
        name: 'Crispy French Fries',
        image: '/placeholder.svg'
      },
      {
        id: 3,
        name: 'Chocolate Milkshake',
        image: '/placeholder.svg'
      }
    ]
  };

  const [reviews, setReviews] = useState(
    orderData.items.map(item => ({
      itemId: item.id,
      rating: 0,
      comment: '',
      photos: []
    }))
  );

  const [overallRating, setOverallRating] = useState(0);
  const [overallComment, setOverallComment] = useState('');

  const updateItemRating = (itemId: number, rating: number) => {
    setReviews(reviews.map(review =>
      review.itemId === itemId ? { ...review, rating } : review
    ));
  };

  const updateItemComment = (itemId: number, comment: string) => {
    setReviews(reviews.map(review =>
      review.itemId === itemId ? { ...review, comment } : review
    ));
  };

  const renderStars = (rating: number, onStarClick: (star: number) => void, size = "h-6 w-6") => {
    return (
      <div className="flex gap-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            onClick={() => onStarClick(star)}
            className="transition-colors"
          >
            <Star
              className={`${size} ${
                star <= rating
                  ? 'fill-yellow-400 text-yellow-400'
                  : 'text-gray-300'
              }`}
            />
          </button>
        ))}
      </div>
    );
  };

  const handleSubmit = () => {
    const hasAllRatings = reviews.every(review => review.rating > 0) && overallRating > 0;

    if (!hasAllRatings) {
      toast({
        title: "Please rate all items",
        description: "Please provide ratings for all items and overall experience.",
        variant: "destructive"
      });
      return;
    }

    toast({
      title: "Thank you for your review!",
      description: "Your feedback helps us improve our service.",
    });

    setLocation('/orders');
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="sticky top-0 z-50 bg-background border-b px-4 py-3">
        <div className="flex items-center gap-3">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation('/orders')}
            className="shrink-0"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <div>
            <h1 className="text-lg font-semibold">Rate & Review</h1>
            <p className="text-sm text-muted-foreground">Order #{orderData.id}</p>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="p-4 space-y-6">
        {/* Overall Rating */}
        <Card>
          <CardContent className="p-4">
            <h2 className="font-semibold mb-3">Overall Experience</h2>
            <div className="text-center space-y-3">
              {renderStars(overallRating, setOverallRating, "h-8 w-8")}
              <p className="text-sm text-muted-foreground">
                {overallRating === 0 && "Tap to rate"}
                {overallRating === 1 && "Poor"}
                {overallRating === 2 && "Fair"}
                {overallRating === 3 && "Good"}
                {overallRating === 4 && "Very Good"}
                {overallRating === 5 && "Excellent"}
              </p>
            </div>
            <Textarea
              placeholder="Share your overall experience..."
              value={overallComment}
              onChange={(e) => setOverallComment(e.target.value)}
              className="mt-3"
              rows={3}
            />
          </CardContent>
        </Card>

        {/* Individual Items */}
        <div className="space-y-4">
          <h2 className="font-semibold">Rate Individual Items</h2>

          {orderData.items.map((item) => {
            const review = reviews.find(r => r.itemId === item.id);

            return (
              <Card key={item.id}>
                <CardContent className="p-4">
                  <div className="flex gap-3 mb-3">
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-12 h-12 rounded-lg object-cover"
                    />
                    <div className="flex-1">
                      <h3 className="font-medium">{item.name}</h3>
                      <div className="mt-1">
                        {renderStars(
                          review?.rating || 0,
                          (rating) => updateItemRating(item.id, rating)
                        )}
                      </div>
                    </div>
                  </div>

                  <Textarea
                    placeholder={`What did you think of the ${item.name}?`}
                    value={review?.comment || ''}
                    onChange={(e) => updateItemComment(item.id, e.target.value)}
                    rows={2}
                    className="mb-3"
                  />

                  <Button
                    variant="outline"
                    size="sm"
                    className="w-full"
                    onClick={() => {
                      toast({
                        title: "Photo upload",
                        description: "Photo upload feature coming soon!",
                      });
                    }}
                  >
                    <Camera className="h-4 w-4 mr-2" />
                    Add Photos (Optional)
                  </Button>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {/* Tips Section */}
        <Card className="bg-accent/50">
          <CardContent className="p-4">
            <h3 className="font-medium mb-2">Help others decide</h3>
            <ul className="text-sm text-muted-foreground space-y-1">
              <li>‚Ä¢ Share what you liked or didn't like</li>
              <li>‚Ä¢ Mention food quality, taste, and portion size</li>
              <li>‚Ä¢ Add photos to show how your food looked</li>
            </ul>
          </CardContent>
        </Card>
      </div>

      {/* Bottom Action */}
      <div className="sticky bottom-0 bg-background border-t p-4">
        <Button
          onClick={handleSubmit}
          className="w-full"
          variant="food"
          size="mobile"
        >
          <Send className="h-4 w-4 mr-2" />
          Submit Review
        </Button>
      </div>
    </div>
  );
};

export default RateReviewPage;

client/src/components/ReorderPage.tsx.content:
import React, { useState } from 'react';
import { useLocation, useSearch } from 'wouter';
import { ArrowLeft, Plus, Minus, ShoppingCart } from 'lucide-react';
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";

const ReorderPage = () => {
  const [, setLocation] = useLocation();
  const searchParams = new URLSearchParams(useSearch());
  const orderId = searchParams.get('orderId');
  const { toast } = useToast();

  // Mock order data
  const orderData = {
    id: orderId || 'ORD001',
    items: [
      {
        id: 1,
        name: 'Grilled Chicken Burger',
        price: 12.99,
        image: '/placeholder.svg',
        quantity: 1,
        customizations: ['No onions', 'Extra cheese']
      },
      {
        id: 2,
        name: 'Crispy French Fries',
        price: 4.99,
        image: '/placeholder.svg',
        quantity: 2,
        customizations: []
      },
      {
        id: 3,
        name: 'Chocolate Milkshake',
        price: 5.99,
        image: '/placeholder.svg',
        quantity: 1,
        customizations: ['Extra chocolate']
      }
    ]
  };

  const [items, setItems] = useState(orderData.items.map(item => ({
    ...item,
    reorderQuantity: item.quantity
  })));

  const updateQuantity = (itemId: number, change: number) => {
    setItems(items.map(item => 
      item.id === itemId 
        ? { ...item, reorderQuantity: Math.max(0, item.reorderQuantity + change) }
        : item
    ));
  };

  const getTotalPrice = () => {
    return items.reduce((total, item) => total + (item.price * item.reorderQuantity), 0);
  };

  const getSelectedItemsCount = () => {
    return items.reduce((count, item) => count + item.reorderQuantity, 0);
  };

  const handleReorder = () => {
    const selectedItems = items.filter(item => item.reorderQuantity > 0);
    if (selectedItems.length === 0) {
      toast({
        title: "No items selected",
        description: "Please select at least one item to reorder.",
        variant: "destructive"
      });
      return;
    }

    toast({
      title: "Added to cart!",
      description: `${getSelectedItemsCount()} items added to your cart.`,
    });

    // Navigate to cart or home
    setLocation('/cart');
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="sticky top-0 z-50 bg-background border-b px-4 py-3">
        <div className="flex items-center gap-3">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => window.history.back()}
            className="shrink-0"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <div>
            <h1 className="text-lg font-semibold">Reorder</h1>
            <p className="text-sm text-muted-foreground">Order #{orderData.id}</p>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="p-4 space-y-4">
        <div className="text-center py-2">
          <p className="text-muted-foreground">
            Adjust quantities and tap "Add to Cart" to reorder
          </p>
        </div>

        {/* Items List */}
        <div className="space-y-3">
          {items.map((item) => (
            <Card key={item.id} className="overflow-hidden">
              <CardContent className="p-4">
                <div className="flex gap-3">
                  <img
                    src={item.image}
                    alt={item.name}
                    className="w-16 h-16 rounded-lg object-cover"
                  />

                  <div className="flex-1 min-w-0">
                    <h3 className="font-medium text-sm leading-tight">{item.name}</h3>
                    <p className="text-primary font-semibold mt-1">${item.price}</p>

                    {item.customizations.length > 0 && (
                      <div className="mt-2 flex flex-wrap gap-1">
                        {item.customizations.map((custom, index) => (
                          <Badge key={index} variant="secondary" className="text-xs">
                            {custom}
                          </Badge>
                        ))}
                      </div>
                    )}
                  </div>

                  <div className="flex flex-col items-center gap-2">
                    <div className="flex items-center gap-2 bg-accent rounded-lg p-1">
                      <Button
                        variant="ghost"
                        size="icon"
                        className="h-8 w-8"
                        onClick={() => updateQuantity(item.id, -1)}
                        disabled={item.reorderQuantity === 0}
                      >
                        <Minus className="h-4 w-4" />
                      </Button>

                      <span className="w-8 text-center font-medium">
                        {item.reorderQuantity}
                      </span>

                      <Button
                        variant="ghost"
                        size="icon"
                        className="h-8 w-8"
                        onClick={() => updateQuantity(item.id, 1)}
                      >
                        <Plus className="h-4 w-4" />
                      </Button>
                    </div>

                    <p className="text-xs text-muted-foreground text-center">
                      Originally: {item.quantity}
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>

      {/* Bottom Action */}
      <div className="sticky bottom-0 bg-background border-t p-4">
        <div className="flex items-center justify-between mb-3">
          <div>
            <p className="font-semibold">Total: ${getTotalPrice().toFixed(2)}</p>
            <p className="text-sm text-muted-foreground">
              {getSelectedItemsCount()} items selected
            </p>
          </div>
        </div>

        <Button 
          onClick={handleReorder}
          className="w-full"
          variant="food"
          size="mobile"
        >
          <ShoppingCart className="h-4 w-4 mr-2" />
          Add to Cart
        </Button>
      </div>
    </div>
  );
};

export default ReorderPage;

client/src/components/SearchPage.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, Search, Star } from "lucide-react";

export default function SearchPage() {
  const [, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");

  const allItems = [
    { id: 1, name: "Veg Thali", price: 85, rating: 4.5, category: "Main Course", isVeg: true },
    { id: 2, name: "Masala Dosa", price: 60, rating: 4.3, category: "South Indian", isVeg: true },
    { id: 3, name: "Chole Bhature", price: 70, rating: 4.6, category: "North Indian", isVeg: true },
    { id: 4, name: "Samosa", price: 20, rating: 4.2, category: "Snacks", isVeg: true },
    { id: 5, name: "Masala Tea", price: 15, rating: 4.5, category: "Beverages", isVeg: true },
    { id: 6, name: "Sandwich", price: 40, rating: 4.0, category: "Snacks", isVeg: true },
    { id: 7, name: "Coffee", price: 20, rating: 4.2, category: "Beverages", isVeg: true },
    { id: 8, name: "Pakora", price: 30, rating: 4.4, category: "Snacks", isVeg: true }
  ];

  const filteredItems = allItems.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.category.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const popularSearches = ["Thali", "Dosa", "Tea", "Snacks", "South Indian"];

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-primary text-primary-foreground px-4 py-4 sticky top-0 z-10">
        <div className="flex items-center space-x-4">
          <Button variant="ghost" size="icon" className="text-white" onClick={() => setLocation('/home')}>
            <ArrowLeft className="w-5 h-5" />
          </Button>
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
            <Input
              className="w-full pl-10 bg-white text-foreground"
              placeholder="Search for food items..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              autoFocus
            />
          </div>
        </div>
      </div>

      <div className="p-4 space-y-4">
        {/* Popular Searches */}
        {!searchQuery && (
          <div>
            <h3 className="font-semibold mb-3">Popular Searches</h3>
            <div className="flex flex-wrap gap-2">
              {popularSearches.map((term) => (
                <Badge
                  key={term}
                  variant="secondary"
                  className="cursor-pointer"
                  onClick={() => setSearchQuery(term)}
                >
                  {term}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Search Results */}
        {searchQuery && (
          <div>
            <h3 className="font-semibold mb-3">
              {filteredItems.length} results for "{searchQuery}"
            </h3>
            <div className="space-y-3">
              {filteredItems.map((item) => (
                <Card
                  key={item.id}
                  className="cursor-pointer hover:shadow-md transition-shadow"
                  onClick={() => setLocation(`/dish/${item.id}`)}
                >
                  <CardContent className="p-4">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <div className="flex items-center space-x-2 mb-1">
                          <h4 className="font-medium">{item.name}</h4>
                          {item.isVeg && (
                            <div className="w-4 h-4 border-2 border-green-500 flex items-center justify-center">
                              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                            </div>
                          )}
                        </div>
                        <p className="text-sm text-muted-foreground mb-2">{item.category}</p>
                        <div className="flex items-center space-x-1">
                          <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
                          <span className="text-sm">{item.rating}</span>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className="font-bold text-lg">‚Çπ{item.price}</p>
                        <Button size="sm" className="mt-2">
                          Add
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        )}

        {/* No Results */}
        {searchQuery && filteredItems.length === 0 && (
          <div className="text-center py-8">
            <Search className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
            <h3 className="font-semibold text-lg mb-2">No items found</h3>
            <p className="text-muted-foreground">
              Try searching for something else or browse our menu
            </p>
            <Button className="mt-4" onClick={() => setLocation("/home")}>
              Browse Menu
            </Button>
          </div>
        )}
      </div>
    </div>
  );
}

